export const validatePassword = (password) => {
  let regularExpression = /^(?=.*[A-Z])(?=.*[a-z])(?=.*[0-9])(?=.*[!@#$%^&*()_+=~?<>,.\\\/;':"-]).{8,}$/;
  return !!password.match(regularExpression);
};

